#!/bin/bash

echo -e "\n"
echo "Presetting sales org config for VN."

updateSalesorgconfigMutationReq=$(curl --location 'https://uat.ezrx.com/api/strapiEngineMutation' \
--header 'Content-Type: application/json' \
--data '{"query":"mutation updateSalesorgconfigMutation($input: UpdateSalesOrgConfigInput!) {           updateSalesOrgConfig(input: $input) {             salesOrgConfig {               salesOrgCode               ponRequired               priceOverride               expiryDateDisplay               enableBatchNumber               currency               netPriceOverride               languageFilter               enableVat               enableZDP5               languageValue               materialWithoutPrice               enableZDP8Override               displayOrderDiscount               enableIRN               enableTaxClassification               disableBundles               disableProcessingStatus               enableCollectiveNumber               enableGimmickMaterial               hideStockDisplay               showPOAttachment               disableDeliveryDate               enableTaxAtTotalLevelOnly               enableGreenDelivery               greenDeliveryDelayInDays               greenDeliveryUserRole               minOrderAmount               hideCustomer               disableOrderType               vatValue               enableSpecialInstructions               enableReferenceNote               enableMobileNumber               enablePaymentTerms               enableGMC               enableListPrice               enableDefaultMD               disablePaymentTermsDisplay               enableBillTo               enableOHPrice               addOosMaterials               oosValue               enableRemarks               enableTaxDisplay               enableGMN                }           }         }","variables":{"input":{"where":{"id":45},"data":{"salesOrgCode":"3050","ponRequired":false,"priceOverride":false,"expiryDateDisplay":false,"batchNumDisplay":false,"enableBatchNumber":false,"currency":"vnd","netPriceOverride":false,"languageFilter":false,"enableVat":true,"enableZDP5":true,"languageValue":"","materialWithoutPrice":false,"enableZDP8Override":false,"displayOrderDiscount":false,"enableIRN":false,"enableTaxClassification":true,"disableBundles":true,"disableProcessingStatus":false,"enableCollectiveNumber":true,"enableGimmickMaterial":true,"hideStockDisplay":false,"showPOAttachment":false,"disableDeliveryDate":true,"enableTaxAtTotalLevelOnly":false,"enableGreenDelivery":false,"greenDeliveryDelayInDays":0,"greenDeliveryUserRole":0,"minOrderAmount":"1000000","hideCustomer":true,"disableOrderType":true,"vatValue":0,"enableSpecialInstructions":true,"enableReferenceNote":false,"enableMobileNumber":false,"enablePaymentTerms":false,"enableGMC":false,"enableListPrice":false,"enableDefaultMD":false,"disablePaymentTermsDisplay":false,"enableBillTo":true,"enableOHPrice":true,"addOosMaterials":true,"oosValue":1,"enableRemarks":false,"enableTaxDisplay":true,"enableGMN":false}}}}')

response=$(echo $updateSalesorgconfigMutationReq)
echo "Response is : "  $response

echo "Your VN sales org config has been successfully reset."
echo -e "\n"